components/products/productView-features:
{{#eachIndex (split product.description '<!-- features -->')}}
{{#if index '==' '1' }} 
<div class="col">
    <div class="block" style="font-size:1.1em;">
        <h4>Features</h4>
        {{{item}}}
    </div>
</div>
{{/if}}
{{/eachIndex}}

components/products/productView-longdesc:
{{#eachIndex (split product.description '<!-- long -->')}}
{{#if index '==' '1' }} 
<div class="col">
    <div class="block" style="font-size:1.1em;">
        {{{item}}}
    </div>
</div>
{{/if}}
{{/eachIndex}}
