components/faceted-search/facets/hierarc:
{{#if items}}
<div class="sidebar-section accordion {{ title }}">
    <h5 class="title">
        <span>{{ title }}</span>
        <i class="sprite sprite-black-plus"></i>
    </h5>
    <div class="content">
        <div id="facetedSearch-content--{{hyphenate facet }}">
            <ul id="facetedSearch-navList--{{hyphenate facet }}" class="navList" data-facet="{{facet}}" data-has-more-results="{{ has_more_results }}">
                {{#each items}}
                    <li class="navList-item">
                        <a
                            href="{{ url }}"
                            class="navList-action navList-action--checkbox {{ title }} {{#if selected }} is-selected {{/if}} {{#if has_sub_categories }} has-children {{/if}}"
                            data-id="{{ id }}"
                            data-faceted-search-facet>
                            {{ title }}
                            {{#if ../show_product_counts}}
                                {{#if count}}
                                    <span>({{ count }})</span>
                                {{/if}}
                            {{/if}}
                        </a>
                        {{#if children}}
                            {{> components/faceted-search/facets/hierarchy-children children}}
                        {{/if}}
                    </li>
                {{/each}}
            </ul>

            {{#if show_more_toggle}}
                <a href="#facetedSearch-navList--{{hyphenate facet }}" role="button" class="toggleLink">
                    <span class="toggleLink-text toggleLink-text--off">{{lang 'search.faceted.show-more'}}</span>
                </a>
            {{/if}}
        </div>
    </div>
</div>
{{/if}}

components/faceted-search/index:
{{{json faceted_search}}}
<div id="facetedSearch" class="facetedSearch sidebarBlock">
    {{> components/faceted-search/selected-facets this.selected}}

    <a href="#facetedSearch-navList" role="button" class="facetedSearch-toggle toggleLink" data-collapsible>
        <span class="facetedSearch-toggle-text">
            {{#if facets.length '>' 2}}
                Refine Search
            {{/if}}

            {{#if facets.length '<=' 2}}
                Refine Search
            {{/if}}
        </span>

        <!-- <span class="facetedSearch-toggle-indicator"> -->
            <span class="toggleLink-text toggleLink-text--on">
                {{lang 'search.faceted.hide-filters'}}

                <i class="sprite sprite-black-plus"></i>
            </span>

            <span class="toggleLink-text toggleLink-text--off">
                {{lang 'search.faceted.show-filters'}}

                <i class="sprite sprite-black-plus"></i>
            </span>
        </span> 
    </a>

    {{#any facets visible=true}}    
        <div
            id="facetedSearch-navList"
            class="facetedSearch-navList blocker-container">


                {{#each facets}}
                    {{#if visible}}                        
                        {{{dynamicComponent 'components/faceted-search/facets'}}}
                    {{/if}}
                {{/each}}

            <div class="blocker" style="display: none;"></div>
        </div>
    {{/any}}
</div>
