layouts/cart.scss:
.cart-list {
    .cart-item-thumb {
        font-size: 5em;
        text-align: center;
        min-height: 200px;
        img {
            display: block;
            width: 100%;
            outline: none;
            border: none;
        }
    }
    .cart-item-title {
        padding: 0 2rem;
        .definitionList {
            width: 100%;
            .definitionList-key {
                width: auto;
                float: left;
                clear: left;
            }
            .definitionList-value {
                float: left;
                margin: 0 0 0 .5rem;
            }
            &:after {
                content: '';
                width: 100%;
                display: block;
                clear: both;
            }
        }
    }
}
.cart-total-wrapper {
    background: $lightgrey;
    height: 100%;
    padding: 2rem;
    ul {
        margin: 0;
        padding: 0;
        li {
            list-style: none;
        }
    }
    .cart-total {
        margin-bottom: 1rem;
        .cart-total-label {
            float: left;
            &:after {
                content: '';
                width: 100%;
                display: block;
                clear: both;
            }
        }
        .cart-total-value {
            float: right;
        }
        &:after {
            content: '';
            width: 100%;
            display: block;
            clear: both;
        }
    }
    .coupon-code {
        clear: both;
        form {
            display: flex;
            input {
                height: 44px;
                line-height: 44px;
                border: none;
                outline: none;
                -webkit-appearance: none;
                appearance: none;
                margin: 0;
                padding: 0;
                &[type="text"]{
                    padding: 0 1rem;
                    background: $darkgrey;
                    color: $white;
                    width: 80%;
                }
                &[type="submit"]{
                    background: $teal;
                    color: $white;
                    border: none;
                    text-transform: uppercase;
                    text-align: center;
                    cursor: pointer;
                    -webkit-appearance: none;
                    appearance: none;
                    width: 20%;
                }
            }
        }
    }
}
.alertBox {
    display: block;
    width: 100%;
    background: $callout;
    padding: 0 1rem;
    margin-bottom: 15px;
    text-align: center;
    p {
        margin: 0;
        font-weight: bold;
        color: $white;
        text-transform: uppercase;
        font-size: 16px;
        position: relative;
        display: flex;
        flex-wrap: wrap;
        justify-content: center;
        align-content: center;
        align-items: center;
        i {
            position: relative;
            transform:  scale(0.5, 0.5);
        }
    }
}

layouts/editorial.scss:
#main-content.editorial {
    .centered {
        margin: 0 auto;
    }
    .small-header {
        display: flex;
        flex-wrap: wrap;
        width: 100%;
        justify-content: space-between;
        align-content: center;
        padding: .5rem 1rem;
        position: fixed;
        top: 0;
        left: 0;
        z-index: 2;
        background: #fff;
        #logo {
            flex: 0 0 250px;
            margin-right: 1rem;
            a {
                display: flex;
                width: 100%;
                height: 100%;
                -ms-flex-line-pack: center;
                align-content: center;
                img {
                    display: block;
                    width: 100%;
                    height: auto;
                    align-self: center;
                    max-width: 250px;
                    margin: 0;
                }
            }
            @media screen and (max-width: 768px) {
                flex: 0 0 200px;
            }
            @media screen and (max-width: 600px) {
                flex: 0 0 170px;
            }
            @media screen and (max-width: 400px) {
                flex: 0 0 120px;
            }
        }
        @media screen and (max-width: 600px) {
            .button-wrap {
                .button {
                    padding: 7px 3rem;
                }
            }
        }
        @media screen and (max-width: 400px) {
            .button-wrap {
                .button {
                    padding: 3px 1.5rem;
                }
            }
        }
    }
    .page-hero {
        margin-top: 53px;
        max-height: 540px;
        @media screen and (max-width: 991px) {
            max-height: 200px;
        }
        @media screen and (max-width: 400px) {
            max-height: 200px;
        }
        @media only screen 
          and (min-device-width: 1024px) 
          and (max-device-width: 1366px) 
          and (-webkit-min-device-pixel-ratio: 2) {
                    max-height: 300px;
          }        
    }
    
    h1 {
        font-size: 4em;
        line-height: 1.5em;
        text-align: left;
        margin-bottom: 1em;
        @media screen and (max-width: 991px) {
            font-size: 3.5em;
            line-height: 1.3em;
            margin-bottom: .5em;
        }
        @media screen and (max-width: 600px) {
            font-size: 3em;
        }
        @media screen and (max-width: 400px) {
            font-size: 2.5em;
        }
    }
    h2 {
        font-size: 2.5em;
        line-height: 1.2em;
        margin-top: 2.5rem;
        margin-bottom: .25em;
        @media screen and (max-width: 600px) {
            font-size: 2em;
        }
        @media screen and (max-width: 400px) {
            font-size: 1.6em;
        }
    }
    h4 {
        font-size: 18px;
        margin-bottom: .5rem;
        @media screen and (max-width: 400px) {
            font-size: 1.6em;
        }
    }
    p, li {
        font-size: 1.3em;
        line-height: 1.6em;
        margin-bottom: 1.5rem;
        a {
            color: $callout;
            &:hover {
                text-decoration: none;
                color: $teal;
            }
        }
        @media screen and (max-width: 768px) {
            font-size: 1.3em;
            line-height: 1.8em;
            margin-bottom: 1.5rem;
        }
        @media screen and (max-width: 600px) {
            font-size: 1.2em;
        }
        @media screen and (max-width: 400px) {
            font-size: 1.1em;
        }
    }
    blockquote {
        border-left: 8px solid $teal;
        color: #707070;
        margin: 0;
        font-size: 20px;
        font-weight: bold;
        padding: 1rem;
        margin-bottom: 1rem;
        font-style: italic;
        cite {
            margin-top: 5px;
            display: block;
            font-weight: normal;
            font-style: normal;
        }
        @media screen and (max-width: 600px) {
            font-size: 1.2em;
        }
    }
    blockquote-v4 {    
        color: $teal;
        text-align: center;
        font-size: 26px;
        font-style: italic;
        cite {
            font-size: 18px;
            color: #707070;
            margin-top: 5px;
            display: block;
            font-weight: normal;
            font-style: normal;
        }
        @media screen and (max-width: 600px) {
            font-size: 1.2em;
        }
    }    
    .mbs-buy-now { 
        .buy-now-product {
            border: 1px solid #ccc;
        } 
        .col-lg-3 {
            @media screen and (max-width: 991px) {
                margin-bottom: 10px;
            }
        }
    }
    .frontline {
        .page-hero {
            text-align: left;             
        }
        .page-title {
            font-size: 8.5rem;
            color: $darkblue;
            width: auto;
            text-align: left;
            font-family: "Roboto Condensed", sans-serif;
            font-weight: 700;
            text-transform: uppercase;
            line-height: 1;
            padding: 2rem 65% 2rem 5rem;
            text-shadow: -1px -1px 1px #efefef, 1px 1px 1px #000;         
            max-width: 99%;   
        }

        .feather {
            height: 2px;                
            background-image: linear-gradient(to right, $teal, $white);
            background-color: $teal; 
            margin-bottom: 15px;            
        }

        .products {
