layouts/reset.scss:
@-ms-viewport {
    width: auto;
}
// =============================================================================
// STENCIL
// =============================================================================

html {
    font-size: 14px;
    font-display: auto;
    letter-spacing: 0.0125rem;
    max-width: 1920px;
    margin: 0 auto;
    overflow-x: hidden;
    &.menu-open {
        height: 100vh;
        overflow: hidden;
        @media(min-width: 993px) {
            height: auto;
            overflow: auto;
        }
    }
}
body {
    margin: 0 auto;
    font-family: $roboto;
    font-display: auto;
    color: $darkgrey;
    line-height: 1.5;
    overflow-x: hidden;
    padding-top: 82px;
}
h1,
h2,
h3,
h4,
h5,
h6 {
	-webkit-font-smoothing: antialiased;
	font-family: $robotoBold;
    font-weight: 700;
    display: block;
}
h1 {
	font-size: 6.5em;
	line-height: 100%;
    margin: 0;
    letter-spacing: 0;
    text-transform: uppercase;
    text-align: center;
    @media screen and (max-width: 1500px) {
        font-size: 6rem;
    }
    @media screen and (max-width: 1350px) {
        font-size: 5.5rem;
    }
    @media screen and (max-width: 1024px) {
        font-size: 5rem;
    }
    @media screen and (max-width: 991px) {
        font-size: 4rem;
    }
    @media screen and (max-width: 767px) {
        font-size: 3rem;
    }
}
h2 {
	font-size: 2.5em;
	line-height: 100%;
    margin: 0 0 0.5rem;
    @media screen and (max-width: 1350px) {
        font-size: 2.2rem;
    }
    @media screen and (max-width: 991px) {
        font-size: 1.8rem;
    }
    @media screen and (max-width: 767px) {
        font-size: 1.6em;
    }
}
h3 {
	font-size: 1.8em;
	line-height: 100%;
    margin: 0 0 0.5rem;
    @media screen and (max-width: 991px) {
        font-size: 1.6rem;
    }
    @media screen and (max-width: 767px) {
        font-size: 1.4em;
    }
}
h4 {
