    }
    @media screen and (max-width: 767px) {
        font-size: 1.2em;
    }
}
h5 {
	font-size: 1.3em;
	line-height: 100%;
    margin: 0 0 0.5rem;
    font-weight: bold;
    @media screen and (max-width: 991px) {
        font-size: 1.2rem;
    }
    @media screen and (max-width: 767px) {
        font-size: 1.1em;
    }
}
p {
	font-size: 1em;
	margin: 0 0 1rem;
	a {
		color: $teal;
		&:hover {
			text-decoration: underline;
		}
    }
    @media screen and (max-width: 767px) {
        font-size: .9em;
    }
}
a {
	color: $darkgrey;
    text-decoration: none;
    cursor: pointer !important;
	&:focus,
	&:hover {
		color: $teal;
		outline: none;
	}
}
.center {
