        background: -moz-linear-gradient(45deg, #142230 0%, #102844 67%, #102844 67%, #010101 100%);
        background: -webkit-linear-gradient(45deg, #142230 0%, #102844 67%, #102844 67%, #010101 100%);
        background: linear-gradient(45deg, #142230 0%, #102844 67%, #102844 67%, #010101 100%);
        filter: progid:DXImageTransform.Microsoft.gradient(startColorstr='#142230', endColorstr='#010101', GradientType=1);
