    }
}

modules/footer/trust.scss:
#trust {
    border-top: 1px solid $darkgrey;
    border-bottom: 1px solid $darkgrey;
    background: $lightgrey;
    padding: 2rem 0;
    .point {
        display: flex;
        flex: 0 0 25%;
        padding: 0;
        box-sizing: border-box;
        border-right: 1px solid $grey;
        align-items: center;
        justify-content: center;
        text-align: center;
        .icon {
            width: 50px;
            height: 50px;
            color: $teal;
            margin: 0 1rem 0 0;
        }
        h3 {
            color: $darkgrey;
