        @media (max-width: 1285px) {
            flex: 0 0 200px;
        }
        @media (max-width: 640px) {
            flex: 0 0 160px;
        }
    }
    #main-menu {
        flex: 2;
        display: flex;
        padding: 0;
        @media (max-width: 992px) {
            justify-content: flex-end;
        }
        ul {
            flex: 1;
            display: flex;
            list-style: none;
            margin: 0;
            height: 100%;
            justify-content: center;
            li {
                line-height: 48px;
                a {
                    height: 100%;
                    color: $darkgrey;
                    text-transform: uppercase;
                    font-family: $roboto;
                    font-weight: bold;
                    font-size: 1.2em;
                    line-height: 48px;
                    display: block;
                    text-align: center;
                    padding: 0 1.5rem;
                    position: relative;
                    .new {
                        color: $callout;
                    }
                    .toggle {
                        display: none;
                        @media (max-width: 992px) {
                            display: block;
                            position: absolute;
                            width: 48px;
                            height: 48px;
                            top: 0;
                            left: 0;
                            i {
                                position: absolute;
                                display: block;
                                top: 50%;
                                left: 0%;
                                transform: translate(-.5em, -50%) scale(0.25, 0.25);
                            }
                        }
                    }
                    @media (max-width: 1575px) {
                        padding: 0 1rem;
                    }
                    @media (max-width: 1430px) {
                        font-size: 1rem;
                    }
                    @media (max-width: 1075px) {
                        padding: 0 .5rem;
                    }
                    @media (max-width: 992px) {
                       text-align: right;
                       padding: 0;
                       font-size: 1.3em;
                    }
                }
                &.home {
                    position: relative;
                    .sprite {
                        position: absolute;
                        display: block;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%,-50%) scale(.4,.4);
                        width: 70px;
                        height: 70px;
                        background-position: -303px -9px;
                        @media (max-width: 992px) {
                            top: 0;
                            left: 0;
                            transform: translate(-0.7em, -11px) scale(0.35, 0.35);
                        }
                    }
                    span {
                        display: none;
                        @media (max-width: 992px) {
                            display: inline-block;
                            margin-left: 1rem;
                        }
                    }
                    @media (max-width: 1175px) {
                        border-left: none;
                    }

                }
                &.customer {
                    display: none;
                    position: relative;
                    ul {
                        display: block;
                        position: relative;
                        top: 0;
                        max-height: 100vh;
                    }
                    @media (max-width: 992px) {
                        display: block;
                    }
                }
                .submenu-wrap {
                    display: block;
                    max-height: 0;
                    position: fixed;
                    top: 48px;
                    left: 0;
                    width: 100%;
                    background: $lightgrey;
                    transition: all .2s ease-in-out;
                    overflow: hidden;
                    z-index: 1;
                    @media (max-width: 992px) {
                        position: relative;
                        max-height: 100vh;
                        top: 0;
                    }
                }
                ul.submenu {
                    display: flex;
                    width: 100%;
                    max-width: 1400px;
                    margin: 0 auto;
                    height: auto;
                    overflow: hidden;
                    padding-bottom: 2rem;
                    li {
                        border: none;
                        flex: 0 0 25%;
                        h5 {
                            text-align: center;
                            margin: 0;
                            padding: 1rem 0;
                            @media (max-width: 992px) {
                                text-align: right;
                                line-height: 1rem;
                                font-size: 1.5em;
                                margin-bottom: 0;
                            }
                        }
                        ul {
                            display: block;
                            border-right: 1px solid $darkgrey;
                            height: 100%;
                            @media (max-width: 992px) {
                                top: 0;
                                border-right: 0;   
                                padding: 0;       
                            }
                            li {
                                a {
                                    font-size: 14px;
                                    line-height: 1.4em;
                                    text-align: left;
                                    font-weight: normal;
                                    text-transform: none;
                                    padding: 0 2rem;
                                    @media (max-width: 992px) {
                                        text-align: right;
                                        padding: 0;
                                    }
                                }
                                &:hover {
                                   > a {
                                        color: $teal;
                                    }
                                }
                                &.first {
                                    margin-bottom: 1rem;
                                    a {
                                        font-weight: bold;
                                    }
                                    @media (max-width: 992px) {
                                        margin-bottom: 0;
                                    }
                                }
                                @media (max-width: 992px) {
                                    padding: 0;
                                }
                            }
                            ul {
                                border-right: 0;
                                margin-left: 1rem;
                                height: auto;
                                @media (max-width: 992px) {
                                    margin: 0 ;
                                }
                            }
                            @media (max-width: 992px) {
                                position: relative;
                                display: block;
                                height: auto;
                            }
                        }
                        &.foot-conditions {
                            flex: 0 0 50%;
                            ul.fifty {
                                width: 50%;
                                float: left;
                                border-right: 0;
                                &:last-of-type {
                                    border-right: 1px solid $darkgrey;
                                }
                                li {
                                    @media (max-width: 992px) {

                                    }
                                }
                                @media (max-width: 992px) {
                                    width: 100%;
                                    margin-bottom: 0;
                                    &:last-of-type {
                                       margin-bottom: 1rem;
                                       border-right: 0;
                                    }
                                }
                            }
                        }
                        .nav-callout {
                            .image {
                                position: relative;
                                a {
                                    padding: 0 1rem;
                                    margin-bottom: 1rem;
                                    position: relative;
                                    h5 {
                                        z-index: 3;
                                        font-size: 18px;
                                        line-height: 100%;
                                        position: absolute;
                                        bottom: 25%;
                                        left: 1rem;
                                        width: calc(100% - 2rem);
                                        padding: .5rem;
                                        text-align: center;
                                        color: #333;
                                        background: rgba(255, 255, 255, 0.7);
                                    }
                                    img {
                                        width: 100%;
                                        max-width: 100%;
                                    }
                                }
                            }
                            @media (max-width: 992px) {
                                display: none;
                             }
                        }
                        @media (max-width: 992px) {
                            padding: 0 0 1rem 0;
                            &.featured {
                                display: none;
                            }
                        }
                    }
                    .button-wrap {
                        a {
                            color: $white;
                            margin: 0 1rem;
                            width: 100%;
                            max-width: 100%;
                        }
                    }
                    @media (max-width: 992px) {
                        position: relative;
                        display: block;
                        max-height: 0;
                        top: 0;
                        padding: 0;
                        overflow: scroll;
                    }
                }
                &:hover {
                    .submenu-wrap {
                        max-height: 100vh;
                        transition: all .2s ease-in-out;
                        z-index: 2;
                    }
                }
                @media (max-width: 1175px) {
                    border-right: none;
                }
                @media (max-width: 992px) {
                    padding: 0 1rem;
                    border-bottom: 1px solid $darkgrey;
                    &.active {
                        ul.submenu {
                            max-height: 100vh;
                            ul {
                                max-height: 100vh;
                            }
                        }
                    }
                }
                &.search {
                    display: none;
                    @media (max-width: 768px) {
                        display: block;
                        padding: 0;
                        #search {
                            margin: 0;
                            display: block;
                            height: 48px;
                            line-height: 48px;
                            form {
                                height: 48px;
                            line-height: 48px;
                            }
                            input {
                                border-radius: 0;
                                height: 48px;
                                line-height: 48px;
                            }
                            button {
                                width: 48px;
                                height: 48px;
                            }
                        }
                    }
                }
            }
            @media (max-width: 992px) {
                order: 3;
                display: block;
                position: fixed;
                top: 82px;
                left: 0;
                width: 100%;
                background: $lightgrey;
                padding: 0;
                max-height: 0;
                overflow: hidden;
                transition: all .2s ease-in-out;
                &.active {
                    max-height: calc(100vh - 82px);
                    overflow-y: auto;
                    transition: all .2s ease-in-out;
                }
            }
        }
        .mobile-menu {
            display: none;
            width: 40px;
            height: 40px;
            line-height: 40px;
            text-align: center;
            margin-left: 5px;
            margin-top: 4px;
            border: 1px solid #333;
            position: relative;
            cursor: pointer;
            .menu-icon-bar {
                display: inline-block;
                position: absolute;
                top: 5px;
                left: 5px;
                width: 26.5px;
                height: 3px;
                border-radius: 2px;
                transition: all 0.2s ease;
                background: #333;
                &:nth-of-type(2) {
                    top: calc(50% - 4px);
                }
                &:nth-of-type(3) {
                    top: calc(100% - 14px);
                }
            }
            .menu-text {
                font-size: 7px;
                position: absolute;
                bottom: 2px;
                display: block;
                width: 100%;
                text-align: center;
                text-transform: uppercase;
                line-height: 7px;
                height: auto;
            }
            @media (max-width: 992px) {
                display: block;
            }
        }
        @import 'cart-wrap';
        @import 'search';
    }
    .wrapper {
        max-width: 1920px;
        margin: 0 auto;
    }
}

modules/header/search.scss:
#search, #searchispbxi_ {
    flex: 0 0 300px;
    display: flex;
    margin: 0;
    align-content: center;
    margin-left: 1rem;
    margin-top: 0px;
    form {
        width: 100%;
        display: flex;
        height: 40px;
        align-self: center;
        input {
            width: calc(100% - 40px);
            height: 40px;
            line-height: 40px;
            padding: 0 1rem 0 2rem;
            color: $white;
            background: $darkgrey !important;
            border-top: 2px solid $teal;
            border-bottom: 2px solid $teal;
            border-left: 2px solid $teal;
            border-right: 0;
            border-radius: 40px 0 0 40px;
            outline: none;
        }
        ::-webkit-input-placeholder { 
            color: white;
        }
        ::-moz-placeholder { 
            color: white;
        }
        :-ms-input-placeholder { 
            color: white;
        }
        :-moz-placeholder {
            color: white;
        }
        button {
            width: 40px;
            height: 40px;
            background: $teal;
            cursor: pointer;
            transition: all .2s ease-in-out;
            -webkit-appearance: none;
            border: none;
            position: relative;
            i {
                position: absolute;
                display: block;
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%) scale(0.25, 0.25);
            }
            &:hover {
                background: $callout;
                transition: all .2s ease-in-out;
            }
        }
    }
    @media (max-width: 1285px) {
        flex: 0 0 250px;
    }
    @media (max-width: 1170px) {
        flex: 0 0 200px;
    }
    @media (max-width: 991px) {
        flex: 0 0 300px;
        margin-top: 4px;
    }
    @media (max-width: 768px) {
        display: none;
    }
}

modules/header/ticker.scss:
.ticker {
    background: $teal;
    padding: 0;
    text-align: center;
    overflow: hidden;
    height: 34px;
    line-height: 34px;
    position: relative;
    width: 100%;
    top: 0;
    .ticker-wrap {
        display: flex;
        height: 34px;
        max-width: 1920px;
        margin: 0 auto;
        padding: 0 1rem;
        .email {
            flex: 1;
            text-align: left;
            color: $white;
            display: flex;
            justify-content: flex-start;
            align-items: center;
            .env {
                position: relative;
                width: 15px;
                display: block;
                margin-right: 10px;
                i {
                    position: absolute;
                    display: block;
                    top: 50%;
                    left: 50%;
                    transform: translate(-50%, -50%) scale(0.2, 0.2);
                }
            }
            @media screen and (max-width: 991px) {
                display: none;
            }
        }
        .ticker-content {
            flex: 4;
            overflow: hidden;
            margin-left: 0;
            margin-right: 1rem;
            display: flex;
            justify-content: center;
            align-items: center;
            p {
                color: $white;
                position: relative;
                font-size: 1em;
                margin: 0;
                @media screen and (max-width: 991px) {
                    font-size: 16px;
                    margin: 0;
                }
                @media screen and (max-width: 767px) {
                    font-size: 13px;
                    line-height: 13px;
                    margin: 0;
                }
            }
            @media screen and (max-width: 991px) {
                margin-right: 0;
            }
        }

        .cart-wrap {
            flex: 1;
            text-align: right;
            display: flex;
            justify-content: flex-end;
            align-content: center;
            align-items: center;
            @media screen and (max-width: 991px) {
                display: flex;
				height: 48px;
				align-items: center;
            }
            .customer,
            .top-cart {
                font-size: 13px;
                text-transform: uppercase;
                transition: all .2s ease-in-out;
                min-width: 25px;
                height: 25px;
                position: relative;
                display: flex;
                justify-content: flex-start;
                .icon-wrap {
                    position: relative;
                    width: 25px;
                    height: 25px;
                    .sprite {
                        position: absolute;
                        display: block;
                        top: 50%;
                        left: 50%;
                        transform: translate(-50%, -50%) scale(0.2, 0.2);
                    }
                }
                span {
                    line-height: 25px;
                    display: block;
                    height: 25px;
                    &.text {
                        padding: 0 5px 0 0;
                    }
                    &.words {
                        padding: 0 10px 0 0;
                        @media screen and (max-width: 1300px) {
                            display: none;
                        }
                    }
                }
            }
            .customer {
                background: $lightgrey;
                span {
                    &.words {
                        text-transform: none;
                    }
                }
                @media screen and (max-width: 991px) {
                    display: none;
                }
            }

            .top-cart {
                background: $darkgrey;
                color: $teal;
                .icon-wrap {
                    position: relative;
                    width: 25px;
                    height: 25px;
                }
                &.active {
                    background: $callout;
                    color: $white;
                }
                &:hover {
                    background: $darkcallout;
                    color: $white;
                }
            }
        }
        @media screen and (max-width: 991px) {
            .contact,
            .cart-wrap {
                display: none;
            }
        }
    }
}

modules/home/blogs.scss:
.home-blogs-container {
    padding-top: 1rem;
    padding-bottom: 0;
    h3 {
        text-align: center;
    }
}
.home-blogs {
    display: flex;
    flex-wrap: wrap;
    padding: 0 .25rem;
    .blog-item {
        position: relative;
        flex: 1;
        margin: .5rem .25rem;
        width: calc(20% - .5rem);
        flex: 0 0 calc(20%  - .5rem);
        overflow: hidden;
        figure {
            margin: 0;
            width: 100%;
            padding-bottom: 200px;
            a {
                width: 100%;
                height: 100%;
                display: block;
                position: relative;
                img {
                    position: absolute;
                    top: 0;
                    left: 0;
                    opacity: 1;
                    transition: all .2s ease-in-out;
                }
            }
        }
        time {
            display: none;
        }
        h3 {
            position: absolute;
            bottom: 1rem;
            left: 0;
            padding: .5rem 2rem .5rem;
            font-size: 16px;
            font-weight: normal;
            background: rgba($white, .8);
        }
        &:hover {
            img {
                opacity: .8;
                transition: all .2s ease-in-out;
            }
            a {
                color: $darkgrey;
            }
        }
        @media screen and (max-width: 1500px) {
            figure {
                padding-bottom: 170px;
            }
        }
        @media screen and (max-width: 1200px) {
            width: calc(25% - .5rem);
            flex: 0 0 calc(25% - .5rem);
            &:nth-child(5){
                display: none;
            }
        }
        @media screen and (max-width: 991px) {
            width: calc(50% - .5rem);
            flex: 0 0 calc(50% - .5rem);
        }
        @media screen and (max-width: 768px) {

        }
    }
}

