modules/icons.scss:
.stars {
    display: flex;
    .star {
        display: block;
        width: 25px;
        height: 25px;
        background: gold;
        border-radius: 100%;
        margin-right: 5px;
        position: relative;
        .sprite-star {
            width: 64px;
            height: 64px;
            background-position: -227px -383px;
            position: absolute;
            top: 50%;
            left: 50%;
            transform: translate(-50%, -50%) scale(0.25, 0.25);
        }
    }
}

.sprite {
    background-image: url('../img/spritesheet.png');
    background-repeat: no-repeat;
    display: block;
}
.sprite-black-chevron-down {
    width: 64px;
    height: 64px;
    background-position: -79px -5px;
