    .productView-image {
        margin: 0;
        img {
            width: 100%;
            height: 100%;
            max-height: 600px;
            max-width: 600px;
        }
    }

    //Product View Thumbnails
    .product-thumbnails {
        transition: all .4s ease-in-out;
