            width: 100px;
            opacity: .6;
            &.active,
            &:hover,
            &:focus {
                opacity: 1;
            }
            img {
                width: 100px;
                height: 100px;
            }
        }
        &.active {
            opacity: 1;
            display: block;
            transition: all .4s ease-in-out;
        }
        .video-figure {
            img {
                padding:0;
                padding-top: 1em;
                width: 40%;
            }
        }
    }
}

modules/products/productView-options.scs:
.productView-options {
	.shipping-returns-box {
		display: inline-block;
		padding: 0 1rem;
		.shipping,
		.returns {
			display: flex;
			margin-bottom: -2rem;
			a {
				display: flex;
				img {
					display: inline-block;
					height: 75%;
				}
				span {
					display: inline-block;
					padding-top: 30px;
					font-size: 1.25rem;
					text-decoration: underline;
				}
			}
		}
	}
	form {
		.product-option-list {
			.size-chart {
				a {
					color: $callout;
					text-transform: uppercase;
					font-weight: bold;
				}
			}
			.option-wrap {
				display: flex;
				flex-wrap: wrap;
				margin-bottom: 1rem;
				background: $lightgrey;
				padding: 0.25rem;
				position: relative;
				.option {
					width: calc(25% - 0.5rem);
					flex: calc(25% - 0.5rem) 0 0;
					margin: 0 0.25rem;
					label {
						display: inline-block;
						color: $darkgrey;
						background-color: $white;
						transition: all 0.3s ease-in-out 0s;
						overflow: hidden;
						cursor: pointer;
						transition: all 0.2s ease-in-out;
						width: 100%;
						span {
							display: block;
							font-size: 1rem;
							min-width: 100%;
							padding: 1rem 0;
							pointer-events: none;
							text-align: center;
							height: 100%;
							background-size: contain;
							background-repeat: no-repeat;
							background-position: center center;
							transition: all 0.2s ease-in-out;
						}
						&.unavailable {
							border: 1px solid $grey inset;
							color: $lightgrey;
							transition: all 0.2s ease-in-out;
							opacity: 0.2;
							cursor: not-allowed;
							span {
								background: $grey;
							}
						}
					}
					input {
						border: none;
						clip: rect(1px, 1px, 1px, 1px);
						height: 1px;
						overflow: hidden;
						padding: 0;
						position: absolute;
						top: 93%;
						left: 40%;
						width: 1px;
						cursor: pointer;
						&:checked + label,
						&:hover + label {
							border: 1px solid $teal inset;
							transition: all 0.2s ease-in-out;
							span {
								color: $white;
								transition: all 0.2s ease-in-out;
								background-color: $teal;
							}
							&.unavailable {
								border: 1px solid $grey inset;
								background: $grey;
							}
						}
					}
					&:after {
						content: "";
						flex: auto;
					}
					.swatch-label {
						display: block;
						width: 100%;
						text-align: center;
					}
				}
				&.Width {
					.option {
						width: calc(33% - 0.5rem);
						flex: calc(33% - 0.5rem) 0 0;
					}
				}
				&.Color {
					.option {
						width: calc(33% - 0.5rem);
						flex: calc(33% - 0.5rem) 0 0;
						label {
							height: 60px;
							padding: 0;
							display: flex;
							flex-direction: column;
						}
					}
				}
				&.Type {
					.option {
						width: calc(33% - 0.5rem);
						flex: calc(33% - 0.5rem) 0 0;
						label {
							height: 90px;
							padding: 0;
							display: flex;
							flex-direction: column;
						}
						input:checked + label span,
						input:hover + label span {
							background-color: $white;
							background-position: center center;
							border: 3px solid $teal inset;
						}
					}
				}
			}
			label.form-label {
				margin: 0 0.5rem;
				padding-bottom: 0.25rem;
				margin-bottom: 0.25rem;
				text-transform: uppercase;
				font-weight: bold;
				small {
					display: none;
				}
			}
			.form-field--increments {
				display: none;
			}
			.productView-size-help {
				text-align: left;
				padding: 0.5rem;
				p {
					margin: 0;
					text-align: center;
					a {
						text-decoration: underline;
					}
				}
			}
			.select-wrap {
				position: relative;
				height: 44px;
				background: $darkgrey;
				margin: 1rem 0.5rem;
				i {
					position: absolute;
					display: block;
					top: 50%;
					right: 0%;
					transform: translate(0%, -50%) scale(0.25, 0.25);
					z-index: 0;
				}
				select {
					position: relative;
					appearance: none;
					width: calc(100% - 1rem);
					width: calc(100% - 1rem);
					display: block;
					margin: 1rem 0.5rem;
					padding: 1rem;
					color: $white;
					background: transparent;
					text-transform: uppercase;
					border: none;
					outline: none;
					z-index: 1;
					option {
						background: $darkgrey;
					}
				}
			}
		}
	}
	.productView-price-saved {
		margin: 0 0.5rem;
		background: $teal;
		p {
			margin: 0;
			color: $white;
			text-align: center;
			font-weight: bold;
		}
	}

	.action {
		display: block;
		justify-content: center;
		align-items: center;

		margin: 0 0.5rem;
		.action-disclamer {
			display: block;
			color: $callout;
			font-weight: 700;
			text-align: center;
		}
		@media screen and (max-width: 769px) {
			display: block;
		}

		.cart-action {
			width: 100%;
			text-align: center;
			.button-wrap {
				margin: 0;
				button {
					margin: 0;
					flex: 50% 0 0;
				}
				@media screen and (max-width: 769px) {
					display: flex;
				}
			}
		}
	}
	&.A-Wave,
	&.DynaFlange.Orthotic.Support {
		form {
			.product-option-list {
				.option-wrap {
					&.Size {
						.option {
							width: calc(50% - 0.5rem);
							flex: calc(50% - 0.5rem) 0 0;
							span {
								padding: 0.5em;
							}
						}
					}
				}
			}
		}
	}
}

.productView-header {
	display: block;
	.yotpo {
		padding-top: 10px !important;
		&.bottomLine {
			padding-left: 1rem;
		}
		.text-m.write-review-btn-hidden {
			padding-left: 0.5em;
		}
	}
	.productView-price {
		padding: 0 1rem;
		text-align: right;
		#price {
			font-size: 1.5rem;
			.sales-price {
				color: $callout;
			}
			.regular-price {
				color: $black;
				&.sale {
					text-decoration: line-through;
					color: #777;
				}
			}
		}
		#afffirm-promo-box {
			transition: all 0.2s ease-in-out;
			.affirm-as-low-as {
				text-align: right !important;
				font-size: 0.9rem !important;
			}
		}
	}
}

modules/products/productView-video.scss:
.productView-video {
    background: $blue; 
    background: -moz-linear-gradient(-45deg, $blue 0%, $darkblue 100%); 
    background: -webkit-linear-gradient(-45deg, $blue 0%, $darkblue 100%);
    background: linear-gradient(135deg,  $blue 0%, $darkblue 100%); 
    filter: progid:DXImageTransform.Microsoft.gradient( startColorstr='$blue', endColorstr='$darkblue',GradientType=1 ); 
    h2 {
        text-align: center;
        color: $white;
    }
    iframe {
        display: block;
        margin: 0 auto;
        width: 100%;
        max-width: 640px;
        @media screen and (max-width: 769px) {
            max-width: calc(100% - 4rem);
        }
    }
}

modules/sales-trust.scss:
#sales-trust {
    background: $lightgrey;
    padding: .5rem 9rem;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-wrap: wrap;
    .point {
        position: relative;
        display: flex;
        margin: 0 1rem;
        width: calc(33% - 2rem);
        flex: 0 0 calc(33% - 2rem);
        padding: .5rem;
        box-sizing: border-box;
        border-right: 1px solid $grey;
        align-items: center;
        justify-content: flex-start;
        text-align: left;
        border: 1px solid $grey;
        background: $white;
        box-sizing: border-box;
        .icon {
            width: 50px;
            height: 50px;
            color: $teal;
            margin: 0 1rem 0 0;
            @media screen and (max-width: 1200px) {
                width: 40px;
                height: 40px;
                margin: 0 .5rem 0 0;
            }
        }
        h3 {
            color: $darkgrey;
            transition: color .6s ease-in-out;
            margin: 0;
            @media screen and (max-width: 1200px) {
                font-size: 18px;
            }
        }
        p {
            display: block;
            width: 100%;
            color: $darkgrey;
            margin: 0;
            @media screen and (max-width: 1200px) {
                font-size: 12px;
            }
        }
        &:hover {
            h3 {
                color: $callout;
                transition: color .2s ease-in-out;
            }
        }
        .right {
            position: absolute;
            top: 0;
            right: 0;
            width: 30px;
            height: 100%;
            line-height: 64px;
            font-size: 20px;
            color: $white;
            background: $teal;
            text-align: center;
            i {
                position: absolute;
                display: block;
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%) scale(.3, .3);
            }
        }
        @media screen and (max-width: 1200px) {
            margin: 0 .5rem;
            width: calc(33% - 1rem);
            flex: 0 0 calc(33% - 1rem);
        }
        @media screen and (max-width: 1024px) {
            margin: 0 0 .5rem;
            width: 100%;
            flex: 0 0 100%;
        }
        
    }
    @media screen and (max-width: 1500px) {
        padding: .5rem 6rem;
    }
    @media screen and (max-width: 1400px) {
        padding: .5rem 3rem;
    }
    @media screen and (max-width: 1300px) {
        padding: .5rem 0rem;
    }
    @media screen and (max-width: 1024px) {
        padding: .5rem .5rem 0;
    }
}

modules/sidebar.scss:
.sidebar-container {
	.actionBar {
		.form-fieldset {
			border: none;
			padding: 0;
		}
		.form-field {
			margin-bottom: 1rem;
			&.select {
				position: relative;
				i {
					position: absolute;
					top: 50%;
					right: 0;
					z-index: 0;
					width: 64px;
					height: 64px;
					transform: translate(0%, -50%) scale(0.3, 0.3);
				}
			}
		}
		label {
			display: none;
		}
	}
	.form-select {
		display: block;
		width: 100%;
		background: transparent;
		-moz-appearance: none;
		appearance: none;
		-webkit-appearance: none;
		outline: 0;
		border-color: $grey;
		border-radius: 0;
		height: 100%;
		padding: 1rem;
		margin-bottom: 1rem;
		position: relative;
	}

	#faceted-search-container {
		padding: 0;
	}

	#facetedSearch {
		margin-bottom: 1rem;
		a.facetedSearch-toggle {
			display: none;
		}
		#facetedSearch-navList {
			div:visible:last {
				.content {
					border-bottom: 1px solid #ccc;
				}
			}
		}
		.sidebar-section {
			text-align: left;
			margin-bottom: 0;
			padding: 0;
			.title {
				display: block;
				background: $lightgrey;
				color: $darkgrey;
				padding: 0.5rem 1rem;
				cursor: pointer;
				display: block;
				width: 100%;
				position: relative;

				border: 1px solid #ccc;
				border-bottom: none;
				margin-bottom: 0 !important;
				i {
					float: right;
					color: #ccc;
					font-size: 2rem;
					line-height: 0.75;
					&:before {
						content: "\f107";
					}
				}
			}
			.content {
				padding: 1rem;
				overflow: visible;
				max-height: 800px;
				background: $white;
				transition: max-height 1s ease;
				ul {
					list-style: none;
					padding: 0;
				}
			}
			&.refine {
				.content {
					border: 1px solid #ccc !important;
					border-bottom: none !important;
				}
			}
			&.accordion {
				.content {
					padding: 0 1rem;
					max-height: 0;
					overflow: hidden;
					border: 0;
					transition: max-height 0.2s ease;
					background: $white;
					border-left: 1px solid #ccc;
					border-right: 1px solid #ccc;
				}
				&.active {
					.content {
						padding: 1rem;
						overflow: visible;
						max-height: 800px;
						border-top: 1px solid #ccc;
					}
					.title {
						i {
							&:before {
								content: "\f106";
							}
						}
					}
				}
				&.Color,
				&.Size {
					ul {
						li {
							display: inline-block;
							margin: 0.5rem;
							a {
								border: 1px solid #ccc;
								padding: 0.5rem;
								display: inline-block;
								text-align: center;
							}
						}
					}
				}

				&.Size {
					ul {
						li {
							a {
								min-width: 3rem;
								&.is-selected {
									background-color: $teal;
									color: $lightgrey;
									font-weight: 700;
								}
							}
						}
					}
				}

				&.Color {
					ul {
						li {
							a {
								width: 2rem;
								height: 2rem;
								&.is-selected {
									box-shadow: 0 0 4px #800000;
								}
							}
						}
					}
				}
				&.Price,
				&.In.Stock {
					display: none;
				}
			}
		}
	}
}

#facetedSearch-navList .accordion:nth-last-child(3) {
	.title {
		border-bottom: 1px solid #ccc;
	}
	&.active {
		.title {
			border-bottom: none;
		}
		.content {
			border-bottom: 1px solid #ccc;
		}
	}
}

modules/social-links.scss:
.socialLinks {
    display: inline;
    margin: 0 .5rem 0 0;
    padding: 0;
    height: 40px;
    line-height: 40px;
    float: left;
    &:before,
    &:after {
        display: none;
    }
    li {
        height: 40px;
        line-height: 40px;
        display: inline-block;
        a {
            display: block;
            width: 25px;
            height: 25px;
            line-height: 28px;
            background: $teal;
            border-radius: 100%;
            margin-right: .25em;
            margin-top: 7.5px;
            position: relative;
            i {
                position: absolute;
                top: 50%;
                left: 50%;
                margin: 0;
                padding: 0;
                display: block;
                transform: translate(-50%, -50%) scale(0.25, 0.25);
                z-index: 1;
            }
        }
    }
}

modules/styled-reviews.scss:
.styled-reviews {
    background: #67bcb0;
    h2, h3, h4, p {
        color: $white;
        text-align: center;
    }
    .review {
        padding: 2rem 2rem 2rem 5rem;
        margin: 2rem;
        position: relative;
        * {
            text-align: left;
        }
        p {
            font-size: 16px;
            margin-bottom: 0;
            @media screen and (max-width: 640px) {
                font-size: 12px;
            }
        }
        .profile {
            width: 76px;
            height: 76px;
            position: absolute;
            top: 1rem;
            left: -38px;
            img {
                width: 100%;
                height: auto;
            }
        }
        &.red {
            background: #e03b4b;
        }
        &.yellow {
            background: #d5c552;
        }
        &.green {
            background: #5e7b58;
        }
        &.brown {
            background: #5b4c4f;
        }
        @media screen and (max-width: 767px) {
            padding: 1rem 1rem 1rem 3rem;
            margin: 1em 1em 2em 2em;
        }
    }
}

modules/sub-nav.scss:
.subnav {
    padding-top: 2rem;
    padding-bottom: 1rem;
    background: $lightgrey;

    h2 {
        display: block;
        text-align: center;
        text-transform: uppercase;
    }
    p {
        text-align: center;
    }
    .subnav-links {
        display: block;
        width: 100%;
        .subnav-link {
            display: block;
            margin-bottom: 1rem;
            width: 100%;
            .button-wrap {
                .button {
                    max-width: 100%;
                    margin-left: 0;
                    margin-right: 0;
                }
            }
        }
    }
}
@media screen and (max-width: 1199px) {
    .subnav {
        padding-bottom: 0;
        .subnav-links {
            .subnav-link {
                flex: 250px;
            }
        }
    }
}
@media screen and (max-width: 768px) {
    .subnav {
        .subnav-links {
            .subnav-link {
                .button-wrap {
                    padding: 0;
                }
            }
        }
    }
}

modules/tags.scss:
.sale-tag {
    position: absolute;
    top: 0px;
    right: 0px;
    display: block;
    z-index: 2;
    span {
        color: $white;
        font-weight: bold;
        text-align: center;
        text-transform: uppercase;
        z-index: 2;
        display: block;
        width: 100%;
        position: relative;
        z-index: 1;
        width: 43px;
        line-height: 33px;
        padding-right: 8px;
    }
    &:after {
        position: absolute;
        top: 0;
        right: 0;
        content: '';
        width: 0;
        height: 0;
        border-style: solid;
        border-width: 0 70px 70px 0;
        border-color: transparent $callout transparent transparent;
    }
}
.new-tag {
    position: absolute;
    top: 0;
    left: 15px;
    z-index: 2;
    &:after {
        content: '';
        position: absolute;
        top: 0;
        left: 0;
        width: 0;
        height: 0;
        border-style: solid;
        border-width: 100px 100px 0 0;
        border-color: $blue transparent transparent transparent;
    }
    span {
        color: $white;
        font-weight: bold;
        position: absolute;
        top: 11px;
        left: 11px;
        z-index: 3;
        font-size: 18px;
    }
}

modules/testimonials.scss:
#testimonials {
    .testimonial {
        border: 4px solid $teal; 
        padding: 2rem; 
        background: $white; 
        margin-bottom: 2rem; 
        height: 100%;
        @media screen and (max-width: 767px) {
            height: auto;
        }
    }
}

modules/video.scss:
// ===========================
// Video Embedding
// ===========================
.embed-responsive {
    margin: 2rem auto 0;
    position: relative;
    width: 100%;
    padding-bottom: 56.25%!important;
}
.embed-responsive-item,
.embed-responsive iframe {
    position: absolute;
    top: 0;
    left: 0;
    bottom: 0;
    height: 100%;
    width: 100%;
    border: 0;
}
#video {
    .aspect {
        height: 0;
        overflow: hidden;
        position: relative;
        padding-bottom: calc(56.25% / 2);
    }

    iframe {
        position: absolute;
        top: 0;
        left: 0;
        width: 100%;
        height: 100%;
        border: 0;
    }

    .warning {
        padding: 2rem 0 4rem;

        p {
            &.warn-icon {
                position: relative;
                display: table;
                margin: 0 auto;
                padding-left: 30px;

                &:before {
                    content: '';
                    background: url('../img/icon-warning.png') center center no-repeat;
                    background-size: contain;
                    width: 35px;
                    height: 30px;
                    position: absolute;
                    top: -3px;
                    left: -10px;
                }
            }
        }
    }

    .icons {
        text-align: center;

        .icon {
            height: auto;
        }

        p {
            font-size: 12px;
            margin-top: 10px;
            text-align: center;
        }
    }
}
@media screen and (max-width: 1025px) {
    #video {
        .aspect {
            padding-bottom: calc(56.25%);
        }
    }
}
@media screen and (max-width: 769px) {
    #video {
        height: auto;
        iframe,
        * {
            min-height: 200px;
            height: auto;
        }
    }

}
@media screen and (max-width: 551px) {
    #video {
        .icons {
            .icon {
                margin-bottom: 2rem;
                width: 100%;
                height: auto;

                img {
                    max-width: 150px;
                }

                &:last-child {
                    margin: 0;
                }
            }
        }
    }
}

