          height: 2.8125em;
          transform: rotate(45deg);
          border-radius: 50%;
  
          &[class$='left'] {
            top: -.25em;
            left: -.9375em;
            transform: rotate(-45deg);
            transform-origin: 2em 2em;
            border-radius: 4em 0 0 4em;
          }
  
          &[class$='right'] {
            top: -.25em;
            left: .9375em;
            transform-origin: 0 2em;
            border-radius: 0 4em 4em 0;
          }
        }
  
        .swal2-success-ring {
          width: 2em;
          height: 2em;
        }
  
        .swal2-success-fix {
          top: 0;
          left: .4375em;
          width: .4375em;
          height: 2.6875em;
        }
  
        [class^='swal2-success-line'] {
          height: .3125em;
  
          &[class$='tip'] {
            top: 1.125em;
            left: .1875em;
            width: .75em;
          }
  
          &[class$='long'] {
            top: .9375em;
            right: .1875em;
            width: 1.375em;
          }
        }
      }
  
      &.swal2-show {
        animation: showSweetToast .5s;
      }
  
      &.swal2-hide {
        animation: hideSweetToast .2s forwards;
      }
  
      .swal2-animate-success-icon {
        .swal2-success-line-tip {
          animation: animate-toast-success-tip .75s;
        }
  
        .swal2-success-line-long {
          animation: animate-toast-success-long .75s;
        }
      }
    }
  }
  
  // Animations
  @keyframes showSweetToast {
    0% {
      transform: translateY(-.625em) rotateZ(2deg);
      opacity: 0;
    }
  
    33% {
      transform: translateY(0) rotateZ(-2deg);
      opacity: .5;
    }
  
    66% {
      transform: translateY(.3125em) rotateZ(2deg);
      opacity: .7;
    }
  
    100% {
      transform: translateY(0) rotateZ(0);
      opacity: 1;
    }
  }
  
  @keyframes hideSweetToast {
    0% {
      opacity: 1;
    }
  
    33% {
      opacity: .5;
    }
  
    100% {
      transform: rotateZ(1deg);
      opacity: 0;
    }
  }
  
  @keyframes animate-toast-success-tip {
    0% {
      top: .5625em;
      left: .0625em;
      width: 0;
    }
  
    54% {
      top: .125em;
      left: .125em;
      width: 0;
    }
  
    70% {
      top: .625em;
      left: -.25em;
      width: 1.625em;
    }
  
    84% {
      top: 1.0625em;
      left: .75em;
      width: .5em;
    }
  
    100% {
      top: 1.125em;
      left: .1875em;
      width: .75em;
    }
  }
  
  @keyframes animate-toast-success-long {
    0% {
      top: 1.625em;
      right: 1.375em;
      width: 0;
    }
  
    65% {
      top: 1.25em;
      right: .9375em;
      width: 0;
    }
  
    84% {
      top: .9375em;
      right: 0;
      width: 1.125em;
    }
  
    100% {
      top: .9375em;
      right: .1875em;
      width: 1.375em;
    }
  }

